const audioPlayer = document.getElementById('audioPlayer');
const playBtn = document.getElementById('playBtn');
const prevBtn = document.getElementById('prevBtn');
const nextBtn = document.getElementById('nextBtn');
const trackName = document.getElementById('trackName');
const trackArtist = document.getElementById('trackArtist');
const cover = document.getElementById('cover');
const progressBar = document.getElementById('progressBar');
const currentTimeElem = document.getElementById('currentTime');
const durationElem = document.getElementById('duration');

const tracks = [
    {
        name: 'Enna Sona',
        artist: 'Song by Arijit Singh',
        src: './assets/mp3/Enna Sona  Ok Jaanu -(Mr-Jatt.com).mp3',
        cover: 'data:image/jpeg;base64,'
    },
    {
        name: 'Roke Na Ruke Naina',
        artist: 'Song by Arijit Singh',
        src: './assets/mp3/Roke Na Ruke Naina-(Mr-Jatt.com).mp3',
        cover: 'data:image/jpeg;base64,'
    },
    {
        name: 'Tere Mere',
        artist: 'Song by Armaan Malik',
        src: './assets/mp3/Tere Mere - Reprise(WapKing).mp3',
        cover: 'data:image/jpeg;base64,'
    },
    {
        name: 'Zaalima',
        artist: 'Song by Arijit Singh and Harshdeep Kaur',
        src: './assets/mp3/Zaalima Raees-(Mr-Jatt.com).mp3',
        cover: 'data:image/jpeg;base64,'
    }
];

let currentTrackIndex = 0;
let isPlaying = false;
let isDragging = false;

function loadTrack(index) {
    const track = tracks[index];
    audioPlayer.src = track.src;
    trackName.textContent = track.name;
    trackArtist.textContent = track.artist;
    cover.style.backgroundImage = `url(${track.cover})`;

    // Reset progress bar and timer
    progressBar.style.width = `0%`;
    currentTimeElem.textContent = `0:00`;
    durationElem.textContent = `0:00`;

    // Wait until metadata is loaded
    audioPlayer.addEventListener('loadedmetadata', () => {
        durationElem.textContent = formatTime(audioPlayer.duration);
    });

    // Set play button to show play icon initially
    playBtn.innerHTML = '<ion-icon name="play" class="playerIcons"></ion-icon>';
    isPlaying = false;
}

function playTrack(forcePlay = false) {
    if (forcePlay || !isPlaying) {
        audioPlayer.play();
        playBtn.innerHTML = '<ion-icon name="pause" class="playerIcons"></ion-icon>';
        isPlaying = true;
    } else {
        audioPlayer.pause();
        playBtn.innerHTML = '<ion-icon name="play" class="playerIcons"></ion-icon>';
        isPlaying = false;
    }
}

function prevTrack() {
    currentTrackIndex = (currentTrackIndex - 1 + tracks.length) % tracks.length;
    loadTrack(currentTrackIndex, true);
    // Auto play the track
    playTrack(true);
}

function nextTrack() {
    currentTrackIndex = (currentTrackIndex + 1) % tracks.length;
    loadTrack(currentTrackIndex, true);
    // Auto play the track
    playTrack(true);
}

function updateProgressBar() {
    if (!isDragging) {
        const { currentTime, duration } = audioPlayer;
        const progressPercent = (currentTime / duration) * 100;
        progressBar.style.width = `${progressPercent}%`;

        currentTimeElem.textContent = formatTime(currentTime);
        durationElem.textContent = formatTime(duration);
    }
}

function formatTime(time) {
    if (isNaN(time)) return '0:00';
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
}


progressBar.parentElement.addEventListener('click', (e) => {
    if (!isDragging) {
        const rect = progressBar.parentElement.getBoundingClientRect();
        const offsetX = e.clientX - rect.left;
        const newTime = (offsetX / rect.width) * audioPlayer.duration;
        audioPlayer.currentTime = newTime;
    }
});

progressBar.parentElement.addEventListener('mousedown', (e) => {
    isDragging = true;
    onDrag(e);
    document.addEventListener('mousemove', onDrag);
    document.addEventListener('mouseup', stopDrag);
});

function onDrag(e) {
    if (!isDragging) return;
    const rect = progressBar.parentElement.getBoundingClientRect();
    let offsetX = e.clientX - rect.left;
    offsetX = Math.max(0, Math.min(offsetX, rect.width));
    const progressPercent = (offsetX / rect.width) * 100;
    progressBar.style.width = `${progressPercent}%`;
    audioPlayer.currentTime = (offsetX / rect.width) * audioPlayer.duration;
}

function stopDrag() {
    isDragging = false;
    document.removeEventListener('mousemove', onDrag);
    document.removeEventListener('mouseup', stopDrag);
}

playBtn.addEventListener('click', () => playTrack());
prevBtn.addEventListener('click', prevTrack);
nextBtn.addEventListener('click', nextTrack);
audioPlayer.addEventListener('timeupdate', updateProgressBar);
audioPlayer.addEventListener('ended', nextTrack);

loadTrack(currentTrackIndex);


